const appBodega=new Vue({el:"#app-bodega",data:{ventanaNuevo:false,ventanaActualizar:false,filtrarDato:null,costoCompra:{cantidadComprada:0,valorUnidad:null,}},methods:{filtrar(){let tabla=document.querySelector('#cuerpo-tabla');let lista=tabla.children;if(this.filtrarDato!=null){for(incr=0;incr<lista.length;incr++){ lista[incr].classList.add('no-vivible');resultado=lista[incr].getAttribute('data-filtro');if(resultado.indexOf(this.filtrarDato)>-1){lista[incr].classList.remove('no-vivible');}else{lista[incr].classList.add('no-vivible');}}}else{for(incr=0;incr<lista.length;incr++){lista[incr].classList.remove('no-vivible');}}},fecha(){let f=new Date();return f.getDate()+"/"+(f.getMonth()+1)+"/"+f.getFullYear();}}});const appVenta=new Vue({el:"#app-venta",data:{carrito:[],},methods:{agregarCarrito(iten){const alemento=this.carrito.find(elem=>elem.titulo==iten.titulo);if(!alemento){this.carrito.push(iten);}},eliminar(iten){this.carrito.splice(iten,1);}},computed:{total(){let total=0;this.carrito.forEach(function(element,index){total+=element.precioCal;});return total;}}});